import CollectionDoc from './collection-doc';

/**
 * Container class for all metadata pertaining to a user object from the NPR One API
 *
 * @extends {CollectionDoc}
 */
export default class User extends CollectionDoc
{
    /**
     * @param {CollectionDocJSON} json    The decoded JSON object that should be used as the basis for this model
     */
    constructor(json) {
        super(json);
        /** @type {Object}
         * @private */
        this._raw = json;
        /** @type {UserAttributes} */
        this.attributes = {};
        this._hydrate();
    }

    /**
     * Hydrate the internal member variables.
     *
     * @private
     */
    _hydrate() {
        this._validate();
        this.attributes = this._raw.attributes;
    }

    /**
     * Whether this user is a temporary user or not
     *
     * @returns {boolean}
     */
    isTemporary() {
        return parseInt(this.attributes.id, 10) >= 1000000000;
    }

    /**
     * Returns the user's cohort. In most cases, SDK consumers will never need to use this.
     *
     * @returns {UserCohort}
     */
    getCohort() {
        return this.attributes.cohort;
    }

    /**
     * Returns the list of organizations this user is affiliated with. In most cases, you only want a single
     * organization, in which case {@link User#getPrimaryOrganization} should be used.
     *
     * @returns {Array<UserOrganization>}
     */
    getOrganizations() {
        return this.attributes.organizations || [];
    }

    /**
     * Returns the primary, non-NPR organization that this user is affiliated with, or null if no such organization
     * exists.
     *
     * @returns {null|UserOrganization}
     */
    getPrimaryOrganization() {
        const orgs = this.getOrganizations();
        return orgs[0] && orgs[0].id !== '1' ? orgs[0] : null;
    }

    /**
     * Returns the programs, shows, and podcasts that this user has positively interacted with.
     *
     * @returns {Array<UserAffiliation>}
     */
    getAffiliations() {
        return this.attributes.affiliations;
    }

    /**
     * A convenience function to cast this object back to a string, generally only used by the {@link Logger} class.
     * In this case, we return the user's ID.
     *
     * @returns {string}
     */
    toString() {
        return this.attributes.id;
    }

    /**
     * @typedef {Object} UserAttributes
     * @property {string} id Some unique identifier for the user
     * @property {string} email The user's email address
     * @property {string} firstName The user's first name
     * @property {string} lastName The user's last name
     * @property {UserCohort} cohort The user's cohort (an experimental grouping for User Experience A/B Testing)
     * @property {Array<UserOrganization>} organizations User's chosen NPR Member Station(s)
     * @property {Array<UserAffiliation>} affiliations Programs, shows, and podcasts that the user has positively interacted with
     */
    /**
     * @typedef {Object} UserCohort
     * @property {string} id A short ID for this cohort
     * @property {string} name A text string identifying the cohort, useful for metrics
     * @property {string} directory For internal use only; represents the current configuration file being used by the Listening Service
     */
    /**
     * @typedef {Object} UserOrganization
     * @property {string} id Some unique identifier for the organization
     * @property {string} displayName A short displayable text field for the end user, strictly text
     * @property {string} call Station call letters
     * @property {string} city A short description of the station's main market city
     * @property {string} [logo] Station logo image URL
     * @property {string} [donationUrl] The URL to a website where users may make a donation to support the station
     */
    /**
     * @typedef {Object} UserAffiliation
     * @property {number} id A unique identifier for the aggregation (program)
     * @property {string} [title] The display name of the aggregation (program)
     * @property {string} href A link that can be followed to get content from this aggregation (program)
     * @property {boolean} following Whether or not the user is following the aggregation. When changing affiliation status, the client is expected to toggle this value and then send the entire object back.
     * @property {number} [rating] The user's average rating for this affiliation on a scale of 0-1. Absent if user never listened to the aggregation.
     * @property {number} [daysSinceLastListen] The number of days since a user last listened to a story from this aggregation. Absent if user never listened to the aggregation.
     */
}

import Logger from './logger';
import NPROneSDK from './../index';
import ApiError from './../error/api-error';


/**
 * Simulates a delay by wrapping a Promise around JavaScript's native `setTimeout` function.
 *
 * @param {number} ms The amount of time to delay for, in milliseconds
 * @returns {Promise}
 * @private
 */
const delay = ms => new Promise(r => setTimeout(r, ms));


/**
 * A thin wrapper around the Fetch API which provides functionality to automatically
 * request a new access token if an existing one has expired.
 */
export default class FetchUtil
{
    /**
     * Primary workhorse for interacting with the NPR One APIs.
     *
     * @param {string} url
     * @param {Object} [options]
     * @returns {Promise<Object>}
     */
    static nprApiFetch(url, options = {}) {
        Logger.debug(`Starting JSON fetch ${url}`);

        if (!FetchUtil._requestUrlIsAuthorizationCall(url) && !options.headers) {
            options.headers = FetchUtil._getHeaders(); // eslint-disable-line
        }

        return fetch(url, options)
            .then((response) => {
                if (response.ok) {
                    return response.json();
                } else if (response.status === 401 && Boolean(NPROneSDK.accessToken)
                    && !FetchUtil._requestUrlIsAuthorizationCall(url)) {
                    return FetchUtil._attemptAccessTokenRefresh(url, options);
                }
                return FetchUtil.formatErrorResponse(response);
            });
    }

    /**
     * Wraps an error response from an API call in an {@link ApiError} object so that consuming code has more
     * flexibility to determine how to handle the error. To be clear: this function returns a Promise that **always**
     * rejects, but it may or may not have the deserialized JSON body based on whether `response.json()` succeeded or
     * failed (the latter is usually an indicator that the response had an empty body).
     *
     * @param {Response} response
     * @returns {Promise}
     */
    static formatErrorResponse(response) {
        return response.json()
            .then((json) => {
                throw new ApiError(response, json);
            }, (err) => { // this will ONLY catch errors from the deserialization, and not from the line above this
                Logger.debug('Problem deserializing JSON from API error');
                Logger.debug(err);
                throw new ApiError(response);
            });
    }

    /**
     * The logic to attempt an access token refresh, broken out for easier readability.
     *
     * @param {string} url
     * @param {Object} options
     * @returns {Promise<Object>}
     * @private
     */
    static _attemptAccessTokenRefresh(url, options) {
        return NPROneSDK.refreshExistingAccessToken()
            .then(() => { // retry the original request we were making, after a short delay
                const _options = options;
                /* istanbul ignore else */ // defensive coding
                if (options.headers) {
                    _options.headers = FetchUtil._getHeaders(); // make sure we use the new access token
                }

                return delay(250)
                    .then(FetchUtil.nprApiFetch.bind(this, url, _options));
            });
    }

    /**
     * Ensures access token is defined and generates the required Headers object for fetch
     *
     * @returns {Headers}
     * @private
     */
    static _getHeaders() {
        if (NPROneSDK.accessToken === '') {
            throw new Error('An Access Token must set before making API requests.');
        }

        const headers = new Headers();
        headers.append('Authorization', `Bearer ${NPROneSDK.accessToken}`);
        if (NPROneSDK.config.advertisingId) {
            headers.append('X-Advertising-ID', `${NPROneSDK.config.advertisingId}`);
        }
        if (NPROneSDK.config.advertisingTarget) {
            headers.append('X-Advertising-Target', `${NPROneSDK.config.advertisingTarget}`);
        }
        return headers;
    }

    /**
     * Tests whether or not the call to the given URL should be considered an authorization call.
     *
     * @param {string} url
     * @returns {boolean}
     * @private
     */
    static _requestUrlIsAuthorizationCall(url) {
        return (NPROneSDK.config.authProxyBaseUrl && (url.indexOf(NPROneSDK.config.authProxyBaseUrl) > -1))
            || new RegExp(`/authorization/${NPROneSDK.config.apiVersion}`).test(url);
    }
}

import * as Logger from 'js-logger';

/* istanbul ignore next */
Logger.useDefaults({
    formatter(messages, context) {
        messages.unshift('[NPROneSDK]');
        if (context.name) {
            messages.unshift(`[${context.name}]`);
        }
    },
});

/**
 * @typedef {JsLogger} Logger
 */
export default Logger;

import Validator from './validator';
import Recommendation from './../model/recommendation';
import Logger from './../util/logger';


/**
 * Creates recommendation objects from collection doc ensuring that necessary
 * properties are present
 *
 * @param {CollectionDocJSON} collectionDoc
 * @returns {Array<Recommendation>}
 * @throws {TypeError} if the collectionDoc passed in is not valid
 */
export default function createRecommendations(collectionDoc) {
    Validator.validateCollectionDoc(collectionDoc);

    const recommendations = [];
    collectionDoc.items.forEach((item) => {
        try {
            recommendations.push(new Recommendation(item));
        } catch (e) {
            Logger.warn('Recommendation is invalid and has been excluded.', e);
        }
    });

    return recommendations;
}

/**
 * A utility class which performs validations by checking of required object properties exist.
 */
export default class Validator {
    /**
     * Validates collection doc format
     *
     * @param {Object} collectionDoc
     * @throws {TypeError} if collection doc is invalid
     */
    static validateCollectionDoc(collectionDoc) {
        const requiredNames = [
            'version', 'href', 'attributes', 'items', 'links', 'errors',
        ];

        Validator._validate(collectionDoc, requiredNames);
    }

    /**
     * Validates an access token model (obtained from the `POST /authorization/v2/token` endpoint)
     *
     * @param {Object} accessTokenModel
     * @throws {TypeError} if access token model is invalid
     */
    static validateAccessToken(accessTokenModel) {
        const requiredNames = [
            'access_token', 'token_type', 'expires_in',
        ];

        Validator._validate(accessTokenModel, requiredNames);
    }

    /**
     * Validates a device code model (obtained from the `POST /authorization/v2/device` endpoint)
     *
     * @param {Object} deviceCodeModel
     * @throws {TypeError} if device code model is invalid
     */
    static validateDeviceCode(deviceCodeModel) {
        const requiredNames = [
            'user_code', 'verification_uri', 'expires_in', 'interval',
        ];

        Validator._validate(deviceCodeModel, requiredNames);
    }

    /**
     * Base validator function.
     *
     * @param {Object} object
     * @param {Array<string>} requiredNames
     * @throws {TypeError} if object is invalid
     * @private
     */
    static _validate(object, requiredNames) {
        const messages = [];

        for (const name of requiredNames) {
            if (!Validator._has(object, name)) {
                messages.push(`'${name}' is missing and is required.`);
            }
        }

        if (messages.length > 0) {
            throw new TypeError(`${messages.join(', ')} :${JSON.stringify(object)}`);
        }
    }

    /**
     * A typesafe check to make sure the property exists within the object.
     *
     * @param {Object} object
     * @param {string} key
     * @returns {boolean}
     * @private
     */
    static _has(object, key) {
        /* istanbul ignore next: defensive coding, not going to worry too much about this */
        return object ? {}.hasOwnProperty.call(object, key) : false;
    }
}

import chai from 'chai';
import { ACCESS_TOKEN_RESPONSE, DEVICE_CODE_RESPONSE, DEVICE_CODE_POLL_RESPONSE, DEVICE_CODE_DENIED_RESPONSE, DEVICE_CODE_EXPIRED_RESPONSE } from '../../test-data';
import mockery from 'mockery';
import fetchMock from 'fetch-mock';
import Authorization from './../../../src/controller/authorization';
import NprOne from './../../../src/index';
import { testConfig } from '../../test';

const should = chai.should();


/** @test {Authorization} */
describe('Authorization', () => {
    let authorization;
    let refreshTokenUrl;
    let logoutUrl;
    let newDeviceCodeUrl;
    let deviceCodePollUrl;


    beforeEach(() => {
        authorization = new Authorization();
        NprOne.config = testConfig;

        refreshTokenUrl = `^${testConfig.authProxyBaseUrl}${NprOne.config.refreshTokenPath}`;
        logoutUrl = `^${testConfig.authProxyBaseUrl}${NprOne.config.logoutPath}`;
        newDeviceCodeUrl = `${NprOne.config.authProxyBaseUrl}${NprOne.config.newDeviceCodePath}`;
        deviceCodePollUrl = `${NprOne.config.authProxyBaseUrl}${NprOne.config.pollDeviceCodePath}`;
    });

    afterEach(() => {
        fetchMock.restore();
        mockery.deregisterMock('fetch');
    });


    /** @test {Authorization.refreshExistingAccessToken} */
    describe('refreshExistingAccessToken', function () { // intentionally can't use arrow function here
        // see: https://github.com/mochajs/mocha/issues/1763
        this.timeout(16000);

        it('should call the refresh token endpoint in the auth proxy', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(refreshTokenUrl, 'POST', ACCESS_TOKEN_RESPONSE)
                .getMock());

            Authorization.refreshExistingAccessToken()
                .then(() => {
                    fetchMock.called(refreshTokenUrl).should.be.true;
                    fetchMock.calls().unmatched.length.should.equal(0);
                    NprOne.accessToken.should.equal(ACCESS_TOKEN_RESPONSE.access_token);
                    done();
                })
                .catch(done);
        });

        it('should retry the call to the refresh token endpoint in the auth proxy if it receives a bad response the first time', (done) => {
            let numTries = 0;

            const responses = () => {
                numTries += 1;

                if (numTries === 1) {
                    return 500;
                } else {
                    return ACCESS_TOKEN_RESPONSE;
                }
            };

            mockery.registerMock('fetch', fetchMock
                .mock(refreshTokenUrl, 'POST', responses)
                .getMock());

            Authorization.refreshExistingAccessToken()
                .then(() => {
                    fetchMock.called(refreshTokenUrl).should.be.true;
                    fetchMock.calls().matched.length.should.be.greaterThan(1);
                    fetchMock.calls().unmatched.length.should.equal(0);
                    NprOne.accessToken.should.equal(ACCESS_TOKEN_RESPONSE.access_token);
                    done();
                })
                .catch(done);
        });

        it('should retry the call to the refresh token endpoint in the auth proxy up to 3 times, but then error out', (done) => {
            let numTries = 0;

            const responses = () => {
                numTries += 1;

                if (numTries < 5) {
                    return 500;
                } else {
                    return ACCESS_TOKEN_RESPONSE;
                }
            };

            mockery.registerMock('fetch', fetchMock
                .mock(refreshTokenUrl, 'POST', responses)
                .getMock());

            Authorization.refreshExistingAccessToken()
                .then(() => {
                    done('Should not be here, call should have ended on a throw');
                })
                .catch(() => {
                    fetchMock.called(refreshTokenUrl).should.be.true;
                    fetchMock.calls().matched.length.should.be.greaterThan(1);
                    fetchMock.calls().unmatched.length.should.equal(0);
                    done();
                });
        });

        describe('if no auth proxy URL is set', () => {
            beforeEach(() => {
                NprOne.config = { authProxyBaseUrl: '' };
            });

            it('should throw a TypeError', () => {
                chai.expect(() => {
                    Authorization.refreshExistingAccessToken();
                }).to.throw('OAuth proxy not configured. Unable to refresh the access token.');
            });
        });

        describe('if no access token is set', () => {
            beforeEach(() => {
                NprOne.config = { accessToken: '' };
            });

            it('should throw a TypeError', () => {
                chai.expect(() => {
                    Authorization.refreshExistingAccessToken();
                }).to.throw('An access token must be set in order to attempt a refresh.');
            });
        });
    });


    /** @test {Authorization#logout} */
    describe('logout', () => {
        it('should call the logout endpoint in the auth proxy', (done) => {
            const oldAccessToken = NprOne.accessToken;

            mockery.registerMock('fetch', fetchMock
                .mock(logoutUrl, 'POST', JSON.stringify(''))
                .getMock());

            authorization.logout()
                .then(() => {
                    fetchMock.called(logoutUrl).should.be.true;
                    fetchMock.calls().unmatched.length.should.equal(0);
                    const options = fetchMock.lastOptions(logoutUrl);
                    options.body.should.equal(`token=${oldAccessToken}`);
                    NprOne.accessToken.should.equal('');
                    done();
                })
                .catch(done);
        });

        it('should throw an error if the response is not \'ok\'', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(logoutUrl, 'POST', 400)
                .getMock());

            authorization.logout().should.be.rejected.notify(() => {
                fetchMock.called(logoutUrl).should.be.true;
                fetchMock.calls().unmatched.length.should.equal(0);
                done();
            });
        });

        describe('if no access token is set', () => {
            beforeEach(() => {
                NprOne.config = { accessToken: '' };
            });

            it('should throw a TypeError', () => {
                chai.expect(() => {
                    authorization.logout();
                }).to.throw('An access token must be set in order to attempt a logout.');
            });
        });

        describe('if no auth proxy URL is set', () => {
            beforeEach(() => {
                NprOne.config = { authProxyBaseUrl: '' };
            });

            it('should throw a TypeError', () => {
                chai.expect(() => {
                    authorization.logout();
                }).to.throw('OAuth proxy not configured. Unable to securely log out the user.');
            });
        });
    });


    /** @test {Authorization#getDeviceCode} */
    describe('getDeviceCode', () => {
        it('should throw an error if not configured properly', () => {
            NprOne.config = { authProxyBaseUrl: '' };
            chai.expect(() => {
                authorization.getDeviceCode();
            }).to.throw('OAuth proxy not configured. Unable to use the device code.');
        });

        it('should make a request to authProxyUrl when called', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .getMock());

            authorization.getDeviceCode()
                .then((deviceCode) => {
                    fetchMock.called(newDeviceCodeUrl).should.be.true;
                    fetchMock.calls().unmatched.length.should.equal(0);
                    deviceCode.userCode.should.equal(DEVICE_CODE_RESPONSE.user_code);
                    done();
                })
                .catch(done);
        });

        it('should make a request to authProxyUrl with additional scopes when scopes are passed in', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .getMock());

            authorization.getDeviceCode(['listening.readonly', 'identity.readonly'])
                .then(() => {
                    fetchMock.called(newDeviceCodeUrl).should.be.true;
                    const options = fetchMock.lastOptions(newDeviceCodeUrl);
                    options.body.indexOf('listening.readonly').should.not.equal(-1);
                    options.body.indexOf('identity.readonly').should.not.equal(-1);
                    done();
                })
                .catch(done);
        });
    });


    /** @test {Authorization#pollDeviceCode} */
    describe('pollDeviceCode', function () { // intentionally can't use arrow function here
        // see: https://github.com/mochajs/mocha/issues/1763
        this.timeout(16000);

        it('should throw an error if not configured properly', () => {
            NprOne.config = { authProxyBaseUrl: '' };
            chai.expect(() => {
                authorization.pollDeviceCode();
            }).to.throw('OAuth proxy not configured. Unable to use the device code.');
        });

        it('should throw an error if no active device code exists', () => {
            chai.expect(() => {
                authorization.pollDeviceCode();
            }).to.throw('No active device code set. Please call getDeviceCode() before calling this function.');
        });

        it('should return a Promise that rejects if the active device code is expired', (done) => {
            const deviceCodeClone = JSON.parse(JSON.stringify(DEVICE_CODE_RESPONSE));
            deviceCodeClone.expires_in = 0;

            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', deviceCodeClone)
                .getMock());

            authorization.getDeviceCode()
                .then(() => {
                    authorization.pollDeviceCode().should.be.rejectedWith('The device code has expired. Please generate a new one before continuing.').notify(done);
                });
        });

        it('should return a Promise that resolves to a valid access token if device code is valid', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .mock(deviceCodePollUrl, 'POST', ACCESS_TOKEN_RESPONSE)
                .getMock());

            authorization.getDeviceCode()
                .then(() => {
                    return authorization.pollDeviceCode();
                })
                .then((accessToken) => {
                    NprOne.accessToken.should.equal(ACCESS_TOKEN_RESPONSE.access_token);
                    accessToken.toString().should.equal(ACCESS_TOKEN_RESPONSE.access_token);
                })
                .then(done)
                .catch(done);
        });

        it('should return a Promise that rejects if the active device code is invalid (or possibly expired)', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .mock(deviceCodePollUrl, 'POST', {
                    status: 400,
                    body: DEVICE_CODE_EXPIRED_RESPONSE,
                })
                .getMock());

            authorization.getDeviceCode()
                .then(() => {
                    authorization.pollDeviceCode().should.be.rejectedWith('Response status: 400 Bad Request').notify(done);
                });
        });

        it('should return a Promise that rejects if the active device code is valid but the user went to log in and denied the app access', (done) => {
            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .mock(deviceCodePollUrl, 'POST', {
                    status: 401,
                    body: DEVICE_CODE_DENIED_RESPONSE,
                })
                .getMock());

            authorization.getDeviceCode()
                .then(() => {
                    authorization.pollDeviceCode().should.be.rejectedWith('Response status: 401 Unauthorized').notify(done);
                });
        });

        it('should return a Promise that resolves to valid access token after polling a number of times if the user logs in', (done) => {
            let numTries = 0;

            const responses = () => {
                numTries += 1;

                if (numTries < 3) {
                    return {
                        status: 401,
                        body: DEVICE_CODE_POLL_RESPONSE,
                    };
                } else {
                    return ACCESS_TOKEN_RESPONSE; // a.k.a. the user logged in
                }
            };

            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .mock(deviceCodePollUrl, 'POST', responses)
                .getMock());

            authorization.getDeviceCode()
                .then(() => {
                    return authorization.pollDeviceCode();
                })
                .then((accessToken) => {
                    fetchMock.called(deviceCodePollUrl).should.be.true;
                    fetchMock.calls().matched.length.should.be.greaterThan(1);
                    fetchMock.calls().unmatched.length.should.equal(0);
                    NprOne.accessToken.should.equal(ACCESS_TOKEN_RESPONSE.access_token);
                    accessToken.toString().should.equal(ACCESS_TOKEN_RESPONSE.access_token);
                })
                .then(done)
                .catch(done);
        });

        it('should return a Promise that rejects if the returned access token is invalid', (done) => {
            const accessTokenClone = JSON.parse(JSON.stringify(ACCESS_TOKEN_RESPONSE));
            delete accessTokenClone.expires_in;

            mockery.registerMock('fetch', fetchMock
                .mock(newDeviceCodeUrl, 'POST', DEVICE_CODE_RESPONSE)
                .mock(deviceCodePollUrl, 'POST', accessTokenClone)
                .getMock());

            authorization.getDeviceCode()
                .then(() => {
                    authorization.pollDeviceCode().should.be.rejectedWith(`TypeError: 'expires_in' is missing and is required. :${JSON.stringify(accessTokenClone)}`).notify(done);
                });
        });
    });
});

